SELECT    DOC_CODSUC AS RLV_CODSUC,  FCH_DESDE  AS RLV_DESDE,   FCH_HASTA  AS RLV_HASTA,   MIN(DOC_FECHA)        AS RLV_FCHINI,  MAX(DOC_FECHA)        AS RLV_FCHFIN,  SUM(DOC_NETO*DOC_CXC) AS RLV_NETO,    COUNT(*)              AS RLV_CANTID   FROM VIEW_DPDIARIO_QUINCE  INNER JOIN DPDOCCLI    ON DOC_FECHA>=FCH_DESDE AND DOC_FECHA<=FCH_HASTA AND DOC_TIPTRA="D" AND DOC_ACT=1 AND DOC_CXC<>0  INNER JOIN DPTIPDOCCLI ON DOC_TIPDOC=TDC_TIPO AND TDC_LIBVTA=1  GROUP BY DOC_CODSUC,FCH_DESDE,FCH_HASTA  ORDER BY DOC_CODSUC,FCH_DESDE,FCH_HASTA